'use strict';

var dbMgrProvider = require('manage-database');

module.exports = function (config) {
  var db = dbMgrProvider(config);

  return {
    create: function (name) {
      return db.create.bind(db, name);
    },
    drop: function (name) {
      return db.drop.bind(db, name);
    }
  };
};
